Thread demonio = new Thread(() -> {
    while(true) System.out.println("Background");
});
demonio.setDaemon(true);
demonio.start();

class Contador extends Thread {
    int count = 0;
    public void run() {
        while(!isInterrupted()) count++;
    }
}
// Uso:
Contador h1 = new Contador();
h1.setPriority(Thread.MAX_PRIORITY);
h1.start();
